from datetime import datetime

from sqlalchemy import Column, Integer, Sequence, ForeignKey, Boolean, \
    DateTime, text, UniqueConstraint

from fusion_models.fusion.base import FusionBase


class BalancingPreset(FusionBase):
    __tablename__ = 'balancing_presets'

    id = Column(Integer, Sequence('balancing_presets_id_seq'),
                primary_key=True)
    oracle_fbdi_csv_type_id = Column(Integer, nullable=False)
    oracle_fbdi_csv_field_meta_id1 = Column(Integer, ForeignKey(
        'oracle_fbdi_csv_field_meta.id'), nullable=False)
    oracle_fbdi_csv_field_meta_id2 = Column(Integer, ForeignKey(
        'oracle_fbdi_csv_field_meta.id'), nullable=False)
    allow_threshold = Column(Boolean, nullable=False, default=False)

    created_on = Column(DateTime, server_default=text('(now() at time zone '
                                                      '\'utc\')'))
    modified_on = Column(DateTime, onupdate=datetime.utcnow)
    modified_by = Column(Integer, nullable=False)

    __table_args__ = (
        UniqueConstraint(
            'oracle_fbdi_csv_type_id',
            'oracle_fbdi_csv_field_meta_id1',
            'oracle_fbdi_csv_field_meta_id2',
            name='_meta_ids_uc'
        ),
    )
