from datetime import datetime

from sqlalchemy import Column, Integer, Sequence, ForeignKey, Boolean, \
    DateTime, String, text

from fusion_models.fusion.base import FusionBase


class OracleFbdiCsvFieldMeta(FusionBase):
    __tablename__ = 'oracle_fbdi_csv_field_meta'

    id = Column(Integer, Sequence('oracle_fbdi_csv_field_meta_id_seq'),
                primary_key=True)
    oracle_fbdi_csv_type_id = Column(Integer, ForeignKey(
        'oracle_fbdi_csv_types.id'), nullable=False)
    field_name = Column(String, nullable=False)
    display_name = Column(String, nullable=False)
    description = Column(String, nullable=True)
    nullable = Column(Boolean, default=True, nullable=False)
    oracle_data_type = Column(String, nullable=False)
    postgres_data_type = Column(String, nullable=False)
    internal_data_type = Column(String, nullable=False)
    sequence = Column(Integer, nullable=False)
    allow_default = Column(Boolean, default=True, nullable=False)

    created_on = Column(DateTime,
                        server_default=text("(now() at time zone 'utc')"))
    modified_on = Column(DateTime, onupdate=datetime.utcnow)
    modified_by = Column(Integer, ForeignKey('users.id'), nullable=False)
