from datetime import datetime

from sqlalchemy import Column, Integer, Sequence, ForeignKey, String, \
    DateTime, Boolean, UniqueConstraint, text

from fusion_models.fusion.base import FusionBase


class OracleFbdiCsvTypes(FusionBase):
    __tablename__ = 'oracle_fbdi_csv_types'

    id = Column(Integer, Sequence('oracle_fbdi_csv_types_id_seq'),
                primary_key=True)
    oracle_fbdi_type_id = Column(Integer, ForeignKey('oracle_fbdi_types.id'),
                                 nullable=False)
    name = Column(String(128), nullable=False)
    internal_fbdi_table_id = Column(Integer, ForeignKey(
        'internal_fbdi_tables.id'), nullable=False)
    # TODO: oracle_fbdi_interface_table is not used anywhere, remove this
    oracle_fbdi_interface_table = Column(String(128), nullable=False)

    created_on = Column(DateTime,
                        server_default=text("(now() at time zone 'utc')"))
    modified_on = Column(DateTime, onupdate=datetime.utcnow)
    modified_by = Column(Integer, ForeignKey('users.id'), nullable=False)
    is_active = Column(Boolean, default=True)

    __table_args__ = (
        UniqueConstraint('oracle_fbdi_type_id', 'name',
                         name='_oracle_fbdi_csv_types_oracle_fbdi_type_id_'
                              'name_uc'),
        UniqueConstraint(
            'internal_fbdi_table_id',
            name='_oracle_fbdi_csv_types_internal_fbdi_table_id_uc'),
        UniqueConstraint(
            'oracle_fbdi_interface_table',
            name='_oracle_fbdi_csv_types_oracle_fbdi_interface_table_uc'),
    )
