from datetime import datetime

from sqlalchemy import Sequence, Integer, Column, DateTime, \
    ForeignKey, text
from sqlalchemy.dialects.postgresql import JSONB

from fusion_models.fusion.base import FusionBase


class FBDIImportParamsMeta(FusionBase):
    __tablename__ = 'fbdi_import_params_meta'

    id = Column(Integer, Sequence('fbdi_import_params_meta_id_seq'),
                primary_key=True)
    oracle_fbdi_type_id = Column(Integer, ForeignKey('oracle_fbdi_types.id'),
                                 nullable=False)
    meta = Column(JSONB, nullable=False)

    created_on = Column(DateTime,
                        server_default=text("(now() at time zone 'utc')"))
    modified_on = Column(DateTime, onupdate=datetime.utcnow)
    modified_by = Column(Integer, ForeignKey('users.id'), nullable=False)
