from datetime import datetime

from sqlalchemy import Column, Integer, Sequence, ForeignKey, String, \
    DateTime, UniqueConstraint, text

from fusion_models.fusion.base import FusionBase


class OracleJobs(FusionBase):
    __tablename__ = 'oracle_jobs'

    id = Column(Integer, Sequence('oracle_jobs_id_seq'), primary_key=True)
    name = Column(String(128), nullable=False)
    job_package_name = Column(String(256), nullable=False)
    job_definition_name = Column(String(256), nullable=False)
    process_id = Column(Integer, nullable=False)

    created_on = Column(DateTime,
                        server_default=text("(now() at time zone 'utc')"))
    modified_on = Column(DateTime, onupdate=datetime.utcnow)
    modified_by = Column(Integer, ForeignKey('users.id'), nullable=False)

    __table_args__ = (
        UniqueConstraint('name', name='_oracle_jobs_name_uc'),
        UniqueConstraint(
            'job_definition_name', name='_oracle_jobs_job_definition_name_uc'
        ),
        UniqueConstraint('process_id', name='_oracle_jobs_process_id_uc'),
    )
