from datetime import datetime

from sqlalchemy import Sequence, Integer, Column, String, DateTime, \
    ForeignKey, UniqueConstraint, Boolean, text

from fusion_models.fusion.base import FusionBase


class OracleModule(FusionBase):
    __tablename__ = 'oracle_modules'

    id = Column(Integer, Sequence('oracle_modules_id_seq'), primary_key=True)
    name = Column(String(64), nullable=False)
    description = Column(String(256))
    parent_module_id = Column(Integer, ForeignKey('oracle_modules.id'),
                              nullable=False)

    created_on = Column(DateTime,
                        server_default=text("(now() at time zone 'utc')"))
    modified_on = Column(DateTime, onupdate=datetime.utcnow)
    modified_by = Column(Integer, ForeignKey('users.id'), nullable=False)
    is_active = Column(Boolean, default=True)

    __table_args__ = (UniqueConstraint(
        'name', name='_oracle_modules_name_uc'),
    )
