from datetime import datetime

from sqlalchemy import Column, Integer, Sequence, ForeignKey, Boolean, \
    DateTime, String, UniqueConstraint, text

from fusion_models.fusion.base import FusionBase


class OracleVersion(FusionBase):
    __tablename__ = 'oracle_versions'

    id = Column(Integer, Sequence('oracle_versions_id_seq'),
                primary_key=True)
    name = Column(String, nullable=False)
    description = Column(String, nullable=True)

    created_on = Column(DateTime,
                        server_default=text("(now() at time zone 'utc')"))
    modified_on = Column(DateTime, onupdate=datetime.utcnow)
    modified_by = Column(Integer, ForeignKey('users.id'), nullable=False)
    is_active = Column(Boolean, default=True)

    __table_args__ = (UniqueConstraint(
        'name', name='_oracle_versions_name_uc'),
    )
