from datetime import datetime

from sqlalchemy import Column, Integer, Sequence, DateTime, String, text, \
    ForeignKey

from fusion_models.fusion.base import FusionBase


class ReconciliationMeta(FusionBase):
    __tablename__ = 'reconciliation_meta'

    id = Column(Integer, Sequence('reconciliation_meta_id_seq'),
                primary_key=True)
    oracle_fbdi_type_id = Column(Integer, ForeignKey('oracle_fbdi_types.id'),
                                 nullable=False)
    data_sync_preset_id = Column(Integer, ForeignKey('data_sync_presets.id'),
                                 nullable=False)
    reconciliation_report_path = Column(String(256), nullable=False)
    error_report_path = Column(String(256), nullable=False)

    created_on = Column(DateTime,
                        server_default=text("(now() at time zone 'utc')"))
    modified_on = Column(DateTime, onupdate=datetime.utcnow)
    modified_by = Column(Integer, ForeignKey('users.id'), nullable=False)
