from datetime import datetime

from sqlalchemy import Column, Integer, Sequence, DateTime, String, text, \
    ForeignKey, UniqueConstraint
from sqlalchemy.dialects.postgresql import JSONB

from fusion_models.fusion.base import FusionBase


class Frequency(FusionBase):
    __tablename__ = 'frequency'

    id = Column(Integer, Sequence('frequency_id_seq'), primary_key=True)
    name = Column(String(64), nullable=False)
    description = Column(String(256), nullable=True)
    cron_expression = Column(JSONB, nullable=False)

    created_on = Column(DateTime,
                        server_default=text("(now() at time zone 'utc')"))
    modified_on = Column(DateTime, onupdate=datetime.utcnow)
    modified_by = Column(Integer, ForeignKey('users.id'), nullable=False)

    __table_args__ = (
        UniqueConstraint('name', name='_frequency_name_uc'),
        UniqueConstraint('cron_expression',
                         name='_frequency_cron_expression_uc'),
    )
