from datetime import datetime

from sqlalchemy import Column, Integer, Sequence, DateTime, \
    UniqueConstraint, ForeignKey, text
from sqlalchemy.dialects.postgresql import JSONB

from fusion_models.fusion.base import FusionBase


class DataSyncParam(FusionBase):
    __tablename__ = 'data_sync_params'

    id = Column(Integer, Sequence('data_sync_params_id_seq'),
                primary_key=True)
    data_sync_preset_id = Column(Integer, ForeignKey('data_sync_presets.id'),
                                 nullable=False)
    meta = Column(JSONB, nullable=False)

    created_on = Column(DateTime,
                        server_default=text("(now() at time zone 'utc')"))
    modified_on = Column(DateTime, onupdate=datetime.utcnow)
    modified_by = Column(Integer, ForeignKey('users.id'), nullable=False)

    __table_args__ = (
        UniqueConstraint('data_sync_preset_id',
                         name='_data_sync_params_data_sync_preset_id_uc'),
    )
