from datetime import datetime

from sqlalchemy import Sequence, Integer, Column, String, DateTime, \
    ForeignKey, Boolean, UniqueConstraint, text

from fusion_models.fusion.base import FusionBase


class User(FusionBase):
    __tablename__ = 'users'

    id = Column(Integer, Sequence('users_id_seq'), primary_key=True)
    full_name = Column(String(256), nullable=False)
    username = Column(String(256), nullable=False)
    # TODO: put regexp
    # https://stackoverflow.com/questions/44790668/use-regex-in-a-checkconstraint-from-sqlalchemy
    email = Column(String(256), nullable=False)
    password = Column(String(256), nullable=False)

    created_on = Column(DateTime,
                        server_default=text("(now() at time zone 'utc')"))
    modified_on = Column(DateTime, onupdate=datetime.utcnow)
    modified_by = Column(Integer, ForeignKey('users.id'), nullable=False)
    is_active = Column(Boolean, default=True)

    __table_args__ = (UniqueConstraint('username', name='_users_username_uc'),)
