from datetime import datetime

from sqlalchemy import Sequence, Integer, Column, ForeignKey, String, TEXT, \
    DateTime, text, Index
from sqlalchemy.dialects.postgresql import UUID, JSONB

from fusion_models.tenant.base import ClientBase


class BatchProcess(ClientBase):
    __tablename__ = 'batch_processes'

    id = Column(Integer, Sequence('batch_processes_id_seq'),
                primary_key=True)
    batch_id = Column(UUID(as_uuid=True), ForeignKey('batches.id'),
                      nullable=False)
    batch_process_type_id = Column(Integer, nullable=False)
    previous_batch_process_id = Column(Integer,
                                       ForeignKey('batch_processes.id'),
                                       nullable=True)
    status = Column(String(128), nullable=False)
    status_message = Column(TEXT, nullable=True)
    additional_info = Column(JSONB, nullable=True)

    created_on = Column(DateTime,
                        server_default=text("(now() at time zone 'utc')"))
    modified_on = Column(DateTime, onupdate=datetime.utcnow)

    __table_args__ = (
        Index('batch_processes_batch_id_idx', 'batch_id'),
    )
