from datetime import datetime

from sqlalchemy import Column, Integer, Sequence, DateTime, text, ForeignKey, \
    String, Index
from sqlalchemy.dialects.postgresql import UUID, JSONB

from fusion_models.tenant.base import ClientBase


class FBDIRequestBatch(ClientBase):
    __tablename__ = 'fbdi_request_batch'

    id = Column(Integer, Sequence('fbdi_request_batch_id_seq'),
                primary_key=True)
    file_id = Column(UUID, ForeignKey('files.id'), nullable=False)
    status = Column(String(256), nullable=False)
    additional_info = Column(JSONB, nullable=True)
    oracle_process_id = Column(Integer)

    created_on = Column(DateTime,
                        server_default=text("(now() at time zone 'utc')"))
    modified_on = Column(DateTime, onupdate=datetime.utcnow)

    __table_args__ = (
        Index('fbdi_request_batch_file_id_idx', 'file_id'),
    )
