from datetime import datetime

from sqlalchemy import ForeignKey, Column, Integer, String, DateTime, text, \
    Sequence
from sqlalchemy.dialects.postgresql import UUID

from fusion_models.tenant.base import ClientBase


class FileUploadDetail(ClientBase):
    __tablename__ = 'file_upload_details'

    id = Column(Integer, Sequence('file_upload_details_id_seq'),
                primary_key=True)
    file_id = Column(UUID(as_uuid=True), ForeignKey('files.id'),
                     nullable=False)
    batch_process_id = Column(Integer, ForeignKey('batch_processes.id'),
                              nullable=False)
    oracle_ucm_doc_id = Column(Integer, nullable=True)
    oracle_ucm_doc_name = Column(String(64), nullable=True)

    created_on = Column(DateTime,
                        server_default=text("(now() at time zone 'utc')"))
    modified_on = Column(DateTime, onupdate=datetime.utcnow)
