from datetime import datetime

from sqlalchemy import Column, Integer, Sequence, String, DateTime, \
    UniqueConstraint, text, LargeBinary

from fusion_models.tenant.base import ClientBase


class OracleCredential(ClientBase):
    __tablename__ = 'oracle_credentials'

    id = Column(Integer, Sequence('oracle_credentials_id_seq'),
                primary_key=True)
    name = Column(String(64), nullable=False)
    username = Column(String(256), nullable=False)
    password = Column(LargeBinary, nullable=False)

    created_on = Column(DateTime,
                        server_default=text("(now() at time zone 'utc')"))
    modified_on = Column(DateTime, onupdate=datetime.utcnow)
    modified_by = Column(Integer, nullable=False)

    __table_args__ = (
        UniqueConstraint('name', name='_oracle_credentials_name_uc'),
        UniqueConstraint('username', name='_oracle_credentials_username_uc'),
    )
