from datetime import datetime

from sqlalchemy import Column, Integer, Sequence, DateTime, text, \
    UniqueConstraint
from sqlalchemy.dialects.postgresql import JSONB

from fusion_models.tenant.base import ClientBase


class OracleRequestParam(ClientBase):
    __tablename__ = 'oracle_request_params'

    id = Column(Integer, Sequence('oracle_request_params_id_seq'),
                primary_key=True)
    oracle_fbdi_type_id = Column(Integer, nullable=False)
    params = Column(JSONB)

    created_on = Column(DateTime, server_default=text('(now() at time zone '
                                                      '\'utc\')'))
    modified_on = Column(DateTime, onupdate=datetime.utcnow)
    modified_by = Column(Integer, nullable=False)

    __table_args__ = (
        UniqueConstraint(
            'oracle_fbdi_type_id',
            name='_oracle_request_params_oracle_fbdi_type_id_uc'
        ),
    )
