from datetime import datetime

from sqlalchemy import Column, Integer, Sequence, ForeignKey, UniqueConstraint, \
    DateTime, text
from sqlalchemy.dialects.postgresql import ARRAY

from fusion_models.tenant.base import ClientBase


class GroupingRule(ClientBase):
    """
    This rule is probably only used in GL journal import
    """
    __tablename__ = 'grouping_rules'

    id = Column(Integer, Sequence('grouping_rules_id_seq'), primary_key=True)
    mapping_rule_id = Column(Integer, ForeignKey('mapping_rules.id'),
                             nullable=False)
    oracle_fbdi_csv_field_meta_ids = Column(ARRAY(Integer), nullable=False)
    # cannot assign fk constraint to intarray

    created_on = Column(DateTime, server_default=text('(now() at time zone '
                                                      '\'utc\')'))
    modified_on = Column(DateTime, onupdate=datetime.utcnow)
    modified_by = Column(Integer, nullable=False)

    __table_args__ = (
        UniqueConstraint(
            'mapping_rule_id',
            name='_grouping_rules_mapping_rule_id_uc'
        ),
    )
