from datetime import datetime

from sqlalchemy import Column, Integer, Sequence, ForeignKey, DateTime, text, \
    UniqueConstraint, String

from fusion_models.tenant.base import ClientBase


class LookupRule(ClientBase):
    __tablename__ = 'lookup_rules'

    id = Column(Integer, Sequence('lookup_rules_id_seq'), primary_key=True)

    mapping_rule_id = Column(Integer, ForeignKey('mapping_rules.id'),
                             nullable=False)
    oracle_fbdi_csv_field_meta_id = Column(Integer, nullable=False)
    lookup_id = Column(Integer, nullable=False)
    lookup_type = Column(String(16), nullable=False)
    # lookup_type is local/global

    created_on = Column(DateTime, server_default=text('(now() at time zone '
                                                      '\'utc\')'))
    modified_on = Column(DateTime, onupdate=datetime.utcnow)
    modified_by = Column(Integer, nullable=False)

    __table_args__ = (
        UniqueConstraint(
            'mapping_rule_id', 'oracle_fbdi_csv_field_meta_id',
            name='_mapping_rule_id_lookup_target_id_uc'
        ),
    )
