from datetime import datetime

from sqlalchemy import Column, Integer, Sequence, String, DateTime, text, \
    Boolean, UniqueConstraint, ForeignKey

from fusion_models.tenant.base import ClientBase


class SourceSystem(ClientBase):
    __tablename__ = 'source_systems'

    id = Column(Integer, Sequence('source_systems_id_seq'), primary_key=True)
    name = Column(String(64), nullable=False)
    description = Column(String(256))
    sftp_path = Column(String(256))

    created_on = Column(DateTime,
                        server_default=text("(now() at time zone 'utc')"))
    modified_on = Column(DateTime, onupdate=datetime.utcnow)
    modified_by = Column(Integer, nullable=False)

    __table_args__ = (
        UniqueConstraint('name', name='_source_systems_name_uc'),
        UniqueConstraint('sftp_path', name='_source_systems_sftp_path_uc'),
    )


class SourceSystemInterface(ClientBase):
    __tablename__ = 'source_system_interfaces'

    id = Column(Integer, Sequence('source_system_interfaces_id_seq'),
                primary_key=True)
    name = Column(String(64), nullable=False)
    description = Column(String(256))
    sftp_path = Column(String(256))

    source_system_id = Column(Integer, ForeignKey('source_systems.id'),
                              nullable=False)
    oracle_module_id = Column(Integer, nullable=False)

    oracle_credential_id = Column(Integer, ForeignKey('oracle_credentials.id'),
                                  nullable=True)
    oracle_fbdi_type_id = Column(Integer, nullable=False)

    created_on = Column(DateTime,
                        server_default=text("(now() at time zone 'utc')"))
    modified_on = Column(DateTime, onupdate=datetime.utcnow)
    modified_by = Column(Integer, nullable=False)
    is_active = Column(Boolean, default=True)

    __table_args__ = (
        UniqueConstraint('name', name='_source_system_interfaces_name_uc'),
        UniqueConstraint('sftp_path',
                         name='_source_system_interfaces_sftp_path_uc'),
    )
