from datetime import datetime

from sqlalchemy import Column, Integer, Sequence, String, DateTime, \
    UniqueConstraint, text, ForeignKey

from fusion_models.tenant.base import ClientBase


class SourceSystemInterfaceFile(ClientBase):
    __tablename__ = 'source_system_interface_files'

    id = Column(Integer, Sequence('source_system_interface_files_id_seq'),
                primary_key=True)

    name = Column(String(64), nullable=False)
    description = Column(String(256))
    delimiter = Column(String(1), nullable=False)

    source_system_interface_id = Column(Integer, ForeignKey(
        'source_system_interfaces.id'), nullable=False)

    created_on = Column(DateTime, server_default=text('(now() at time zone '
                                                      '\'utc\')'))
    modified_on = Column(DateTime, onupdate=datetime.utcnow)
    modified_by = Column(Integer, nullable=False)

    __table_args__ = (
        UniqueConstraint(
            'name', name='_source_system_interface_files_name_uc'
        ),
    )


class SourceSystemInterfaceFileFields(ClientBase):
    __tablename__ = 'source_system_interface_file_fields'

    id = Column(
        Integer, Sequence('source_system_interface_file_fields_id_seq'),
        primary_key=True
    )

    name = Column(String(64), nullable=False)
    description = Column(String(256))
    sequence = Column(Integer, nullable=False)

    source_system_interface_file_id = Column(Integer, ForeignKey(
        'source_system_interface_files.id'), nullable=False)

    created_on = Column(DateTime, server_default=text('(now() at time zone '
                                                      '\'utc\')'))
    modified_on = Column(DateTime, onupdate=datetime.utcnow)
    modified_by = Column(Integer, nullable=False)

    __table_args__ = (
        UniqueConstraint('name', 'source_system_interface_file_id',
                         name='_fields_source_system_interface_file_id_uc'),
    )
