from datetime import datetime

from sqlalchemy import Column, Integer, Sequence, String, DateTime, Boolean, \
    UniqueConstraint, ForeignKey, text

from fusion_models.tenant.base import ClientBase


class DataSyncConfig(ClientBase):
    __tablename__ = 'data_sync_config'

    id = Column(Integer, Sequence('data_sync_config_id_seq'), primary_key=True)
    data_sync_preset_id = Column(Integer, nullable=False)
    oracle_credential_id = Column(Integer, ForeignKey(
        'oracle_credentials.id'), nullable=False)
    frequency_id = Column(Integer, nullable=False)
    timezone = Column(String(6), nullable=False)
    job_id = Column(Integer, nullable=True)

    created_on = Column(DateTime,
                        server_default=text("(now() at time zone 'utc')"))
    modified_on = Column(DateTime, onupdate=datetime.utcnow)
    modified_by = Column(Integer, nullable=False)
    is_available = Column(Boolean, nullable=False)

    __table_args__ = (
        UniqueConstraint('data_sync_preset_id',
                         name='_data_sync_config_data_sync_preset_id_uc'),
        UniqueConstraint('job_id',
                         name='_data_sync_config_job_id_uc'),
    )
