from datetime import datetime

from sqlalchemy import Column, Integer, Sequence, DateTime, \
    ForeignKey, text, String

from fusion_models.tenant.base import ClientBase


class DataSyncHistory(ClientBase):
    __tablename__ = 'data_sync_history'

    id = Column(Integer, Sequence('data_sync_history_id_seq'),
                primary_key=True)
    data_sync_config_id = Column(Integer, ForeignKey('data_sync_config.id'),
                                 nullable=False)
    status = Column(String(50), nullable=False)
    status_message = Column(String(256), nullable=True)

    created_on = Column(DateTime,
                        server_default=text("(now() at time zone 'utc')"))
    modified_on = Column(DateTime, onupdate=datetime.utcnow)
