from datetime import datetime

from sqlalchemy import Column, Integer, Sequence, DateTime, \
    text, String, UniqueConstraint

from fusion_models.tenant.base import ClientBase


class DataSyncTables(ClientBase):
    __tablename__ = 'data_sync_tables'

    id = Column(Integer, Sequence('data_sync_tables_id_seq'),
                primary_key=True)
    name = Column(String(64), nullable=False)
    description = Column(String(256), nullable=True)

    created_on = Column(DateTime,
                        server_default=text("(now() at time zone 'utc')"))
    modified_on = Column(DateTime, onupdate=datetime.utcnow)
    modified_by = Column(Integer, nullable=False)

    __table_args__ = (
        UniqueConstraint('name', name='_data_sync_tables_name_uc'),
    )
