from datetime import datetime

from sqlalchemy import Column, Integer, Sequence, DateTime, \
    UniqueConstraint, ForeignKey, text
from sqlalchemy.dialects.postgresql import JSONB

from fusion_models.tenant.base import ClientBase


class DataSyncTimestamp(ClientBase):
    __tablename__ = 'data_sync_timestamps'

    id = Column(Integer, Sequence('data_sync_timestamps_id_seq'),
                primary_key=True)
    data_sync_config_id = Column(Integer, ForeignKey('data_sync_config.id'),
                                 nullable=False)
    last_synced_timestamps = Column(JSONB, nullable=False)

    created_on = Column(DateTime,
                        server_default=text("(now() at time zone 'utc')"))
    modified_on = Column(DateTime, onupdate=datetime.utcnow)

    __table_args__ = (
        UniqueConstraint('data_sync_config_id',
                         name='_data_sync_timestamps_data_sync_config_id_uc'),
    )
