from datetime import datetime

from sqlalchemy import Column, Integer, Sequence, String, DateTime, text, \
    ForeignKey, UniqueConstraint
from sqlalchemy.dialects.postgresql import ARRAY

from fusion_models.tenant.base import ClientBase


class Tag(ClientBase):
    __tablename__ = 'tags'

    id = Column(Integer, Sequence('tags_id_seq'), primary_key=True)
    name = Column(String(32), nullable=False)

    created_on = Column(DateTime,
                        server_default=text("(now() at time zone 'utc')"))
    modified_on = Column(DateTime, onupdate=datetime.utcnow)
    modified_by = Column(Integer, nullable=False)

    __table_args__ = (
        UniqueConstraint('name', name='_tags_name_uc'),
    )


class GridTag(ClientBase):
    __tablename__ = 'grid_tags'

    id = Column(Integer, Sequence('grid_tags_id_seq'),
                primary_key=True)
    grid_id = Column(Integer, ForeignKey('grids.id'))
    tag_ids = Column(ARRAY(Integer))

    created_on = Column(DateTime,
                        server_default=text("(now() at time zone 'utc')"))
    modified_on = Column(DateTime, onupdate=datetime.utcnow)
    modified_by = Column(Integer, nullable=False)

    __table_args__ = (
        UniqueConstraint('grid_id', name='_grid_tags_grid_id_uc'),
    )
