from datetime import datetime

from sqlalchemy import Column, Integer, Sequence, String, DateTime, text, \
    UniqueConstraint
from sqlalchemy.dialects.postgresql import JSONB

from fusion_models.tenant.base import ClientBase


class Validation(ClientBase):
    __tablename__ = 'validations'

    id = Column(Integer, Sequence('validations_id_seq'), primary_key=True)
    name = Column(String(64), nullable=False)
    description = Column(String(512))
    type = Column(Integer, nullable=False)
    payload = Column(JSONB)

    created_on = Column(DateTime, server_default=text('(now() at time zone '
                                                      '\'utc\')'))
    modified_on = Column(DateTime, onupdate=datetime.utcnow)
    modified_by = Column(Integer, nullable=False)

    __table_args__ = (
        UniqueConstraint('name', 'type', name='_validations_name_type_uc'),
    )
